pragma solidity ^0.5.0;

contract Blood {
    uint BloodCount=0;

    struct BloodDonation{
        uint id;
        string quantity;
        string person;
    }

    constructor() public {
        createDonation("20",'Bernardo Borges');
    }

    mapping(uint => BloodDonation) public donations;

    function createDonation(string memory _quantity,string memory _person) public {
        BloodCount ++;
        donations[BloodCount] = BloodDonation(BloodCount, _quantity, _person);
    }


}
